	{
		for(int i=2;i<=x/2;i++)
			if (x % 2==0 )
				return false;
		return true;
	}
	public boolean shuixianhua(int x)
	{
	   int i=0,j=0,k=0;
	   i=x / 100;
	   j=(x % 100) /10;
	   k=x % 10;
	   if(x==i*i*i+j*j*j+k*k*k)
		   return true;
	   else
		   return false;

	}
}

